window.onload = function() {
  var navBtn = document.getElementById('nav-btn');
  var navMenu = document.getElementById('nav-menu');

  // 鼠标移入按钮时显示导航菜单
  navBtn.addEventListener('mouseenter', function() {
    navMenu.style.display = 'block';
  });

  // 鼠标移出按钮时隐藏导航菜单
  navBtn.addEventListener('mouseleave', function() {
    // 添加一个延迟，使导航菜单在鼠标移出按钮后保持显示几秒钟
    setTimeout(function() {
      navMenu.style.display = 'none';
    }, 2000);
  });

  // 鼠标移入导航菜单时保持显示
  navMenu.addEventListener('mouseenter', function() {
    clearTimeout(hideMenuTimeout);
  });

  // 鼠标移出导航菜单时隐藏导航菜单
  var hideMenuTimeout;
  navMenu.addEventListener('mouseleave', function() {
    hideMenuTimeout = setTimeout(function() {
      navMenu.style.display = 'none';
    }, 500);
  });
}
const filter = document.getElementById('filter');
const products = document.getElementById('products').querySelectorAll('.col-lg-4 col-md-12');

filter.addEventListener('change', function(event) {
  const category = event.target.value;

  products.forEach(function(product) {
    if (category === 'all' || product.dataset.category === category) {
      product.style.display = 'block';
    } else {
      product.style.display = 'none';
    }
  });
});

